package variableanddatatype;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int a=8;
  int b=20;
  
  //+ operator
  System.out.println(a+b);
  System.out.println(b-a);
  System.out.println(a*b);
  System.out.println(b%a);
	}

}
